package com.uit.cloud.kubernetes;

import java.util.HashMap;
import java.util.List;
import java.util.Map;

import com.github.kubesys.kubernetes.ExtendedKubernetesClient;
import com.github.kubesys.kubernetes.api.model.VirtualMachine;

import io.fabric8.kubernetes.client.Config;
import io.fabric8.kubernetes.client.ConfigBuilder;

/**
 * @author wuheng@otcaix.iscas.ac.cn
 * @since  2019/5/1
 *
 * This code is used to manage CustomResource's lifecycle,
 * such as VirtualMachine
 */
public class ListVirtualMachinesTest {
	
	public static Config config = new ConfigBuilder()
			.withApiVersion("v1")
			.withCaCertData("")
			.withClientCertData("")
			.withClientKeyData("")
			.withMasterUrl("https://10.25.0.137:6443")
			.build();
	
	public static void main(String[] args) throws Exception {

		Map<String, String> labels = new HashMap<String, String>();
		labels.put("henry", "henry");
		
		ExtendedKubernetesClient client = new ExtendedKubernetesClient(config);
		List<VirtualMachine> vms = client.virtualMachines().list(labels).getItems();
		for (VirtualMachine vm : vms) {
			System.out.println(vm);
		}
	}
	
}
